// export default ActivityList

import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'
import { useTable, useSortBy, usePagination } from 'react-table'
import './ActivityList.css' // ÂºïÂÖ•Ëá™ÂÆö‰πâÁöÑ CSS Êñá‰ª∂

// props ÊòØ‰∏Ä‰∏™ÂØπË±°ÔºåÂåÖÂê´‰∫Ü‰ªéÁà∂ÁªÑ‰ª∂‰º†ÈÄíËøáÊù•ÁöÑÊï∞ÊçÆ
function Greeting(props) {
    return <h1>‰Ω†Â•Ω, {props.name}!</h1>
}
function SortableTable({ columns, data }) {
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        page, // ‰ª£Êõø rows Áî®‰∫éÂàÜÈ°µ
        prepareRow,
        canPreviousPage,
        canNextPage,
        pageOptions,
        pageCount,
        gotoPage,
        nextPage,
        previousPage,
        setPageSize,
        state: { pageIndex, pageSize },
    } = useTable(
        {
            columns,
            data,
            initialState: { pageIndex: 0 }, // ÂàùÂßãÈ°µÁ†Å
        },
        useSortBy,
        usePagination // ‰ΩøÁî®ÂàÜÈ°µÊèí‰ª∂
    )

    return (
        <div className="table-container">
            <table {...getTableProps()}>
                <thead>
                    {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                                    {column.render('Header')}
                                    <span>
                                        {column.isSorted
                                            ? column.isSortedDesc
                                                ? ' üîΩ'
                                                : ' üîº'
                                            : ''}
                                    </span>
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {page.map(row => {
                        prepareRow(row)
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map(cell => (
                                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                ))}
                            </tr>
                        )
                    })}
                </tbody>
            </table>
            {/* ÂàÜÈ°µÊéßÂà∂ */}
            <div className="pagination">
                <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
                    {'<<'}
                </button>
                <button onClick={() => previousPage()} disabled={!canPreviousPage}>
                    {'<'}
                </button>
                <button onClick={() => nextPage()} disabled={!canNextPage}>
                    {'>'}
                </button>
                <button onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}>
                    {'>>'}
                </button>
                <span>
                    Page{' '}
                    <strong>
                        {pageIndex + 1} of {pageOptions.length}
                    </strong>
                </span>
                <select
                    value={pageSize}
                    onChange={e => {
                        setPageSize(Number(e.target.value))
                    }}
                >
                    {[10, 20, 30, 40, 50].map(pageSize => (
                        <option key={pageSize} value={pageSize}>
                            Show {pageSize}
                        </option>
                    ))}
                </select>
            </div>
        </div>
    )
}

// ‰ΩøÁî®Á§∫‰æã
const columns = [
    {
        Header: 'ID',
        accessor: 'id', // ËÆøÈóÆÂô®ÊòØ‰ªéÂéüÂßãÊï∞ÊçÆ‰∏≠ÂèñÂá∫Êï∞ÊçÆÁöÑÈîÆ
    },
    {
        Header: 'Ê¥ªÂä®Ê†áÈ¢ò',
        accessor: 'activityTitle',
    },
    {
        Header: 'ÂºÄÂßãÊó∂Èó¥',
        accessor: 'activityStartTime',
    },
    {
        Header: 'ÁªìÊùüÊó∂Èó¥',
        accessor: 'activityEndTime',
    },
    {
        Header: 'ÂèëÂ∏É‰∫∫',
        accessor: 'selectPublisher',
    },
    {
        Header: 'Ê¥ªÂä®Áä∂ÊÄÅ',
        accessor: 'activityStatus',
    },
    {
        Header: 'Áî≥ËØ∑Êà™Ëá≥Êó∂Èó¥',
        accessor: 'applyEndTime',
    },
    {
        Header: 'Êìç‰Ωú',
    },
]

// fetch('./public/activities.json') // ‰ªéÊú¨Âú∞ËµÑÊ∫êÊñá‰ª∂‰∏≠ËØªÂèñÊï∞ÊçÆ
//     .then(response => response.json())
//     .then(data2 => {
//         console.log('read json', data2)
//         data = data2.map(item => {
//             return {
//                 id: item.id,
//                 activityTitle: item.activityTitle,
//                 activityStartTime: item.activityStartTime,
//                 activityEndTime: item.activityEndTime,
//                 selectPublisher: item.selectPublisher,
//                 activityStatus: item.activityStatus,
//                 applyEndTime: item.applyDeadline,
//             }
//         })
//         setData(data)
//     })

function ActivityList() {
    const location = useLocation()
    const res = location.state || {}
    const { name: username, token } = res

    const [data, setData] = useState([])

    useEffect(() => {
        fetch('/activities.json') // Á°Æ‰øùË∑ØÂæÑÊ≠£Á°Æ
            .then(response => response.json())
            .then(data => {
                console.log('read json', data)
                const formattedData = data.result.data.map(item => ({
                    id: item.id,
                    activityTitle: item.activityTitle,
                    activityStartTime: item.activityStartTime,
                    activityEndTime: item.activityEndTime,
                    selectPublisher: item.selectPublisher,
                    activityStatus: item.activityStatus,
                    applyEndTime: item.applyDeadline,
                }))
                setData(formattedData)
            })
    }, [])
    return (
        <div>
            <div>
                <h1>Activity List</h1>
                <Greeting name={username} />
            </div>
            <div>
                <SortableTable data={data} columns={columns} />
            </div>
        </div>
    )
}

export default ActivityList
