            role: '未参与',
            userName: '李春洁',
            activityId: 971,
        },
        {
            id: 36225,
            userId: '23721489',
            role: '未参与',
            userName: '刘瑶',
            activityId: 971,
        },
        {
            id: 36191,
            userId: '23721499',
            role: '未参与',
            userName: '李佳美',
            activityId: 971,
        },
        {
            id: 36196,
            userId: '23721500',
            role: '未参与',
            userName: '张一丹',
            activityId: 971,
        },
        {
            id: 36194,
            userId: '23721501',
            role: '未参与',
            userName: '王波',
            activityId: 971,
        },
        {
            id: 36227,
            userId: '23721533',
            role: '未参与',
            userName: '钱磊',
            activityId: 971,
        },
        {
            id: 36374,
            userId: '23721568',
            role: '未参与',
            userName: '李盈谕',
            activityId: 971,
        },
        {
            id: 36192,
            userId: '23721578',
            role: '未参与',
            userName: '龚梦迪',
            activityId: 971,
        },
    ],
}
function AuditPage() {
    const { id } = useParams()
    const location = useLocation()
    console.log('id', id, 'state', location.state)

    const [selectedFile, setSelectedFile] = useState(null)
    const [uploadStatus, setUploadStatus] = useState('')
    const [isLoading, setIsLoading] = useState(false)
    const [activityData, setActivityData] = useState(null)
    const handleFileChange = event => {
        setSelectedFile(event.target.files[0])
    }
    const delay = ms => new Promise(resolve => setTimeout(resolve, ms))
    const handleUpload = async () => {
        if (selectedFile) {
            // 创建一个 FormData 对象
            const formData = new FormData()
            formData.append('file', selectedFile)
            setIsLoading(true)
            setUploadStatus('上传中')
            try {
                const response = await fetch('http://localhost:3000/upload', {
                    method: 'POST',
                    body: formData,
                })
                await delay(2000)

                if (response.ok) {
                    setUploadStatus(`文件 ${selectedFile.name} 上传成功！`)
                } else {
                    setUploadStatus('文件上传失败！')
                }
            } catch (error) {
                console.error('上传错误:', error)
                setUploadStatus('文件上传失败！')
            } finally {
                setIsLoading(false)
            }
        } else {
            setUploadStatus('请选择一个文件')
        }
    }
    useEffect(() => {
        // 模拟获取活动数据的请求
        async function fetchActivityData() {
            setIsLoading(true)
            try {
                const response = await fetch(`http://localhost:3000/activity/${id}`)
                const data = await response.json()
                data.result = testdata
                if (data.code === 200) {
                    setActivityData(data.result)
                }
            } catch (error) {
                console.error('获取活动数据错误:', error)
            } finally {
                setIsLoading(false)
                setActivityData(testdata)
            }
        }
        fetchActivityData()
    }, [id])
    const columns = [
        {
            title: '序号',
            dataIndex: 'index',
            key: 'index',
            render: (text, record, index) => index + 1,
            align: 'center',
        },
        {
            title: '学号',
            dataIndex: 'userId',
            key: 'userId',
            align: 'center',
        },
        {
            title: '姓名',
            dataIndex: 'userName',
            key: 'userName',
            align: 'center',
        },
        {
            title: '当前参与角色',
            dataIndex: 'role',
            key: 'role',
            align: 'center',
        },
    ]
    return (
        <div>
            <h2>审核页面, 正在审核的活动ID：{id}</h2>
            <div className="upload-section">
                <input
                    type="file"
                    accept=".pdf,image/*"
                    onChange={handleFileChange}
                    className="custom-input"
                />
                <Button onClick={handleUpload}>上传文件</Button>
            </div>
            {selectedFile && (
                <div className="status-message">
                    <p>选中的文件: {selectedFile.name}</p>
                </div>
            )}
            {uploadStatus && (
                <div className="status-message">
                    <p>{uploadStatus}</p>
                </div>
            )}
            {isLoading && (
                <div className="status-message">
                    <p>加载中...</p>
                </div>
            )}
            <div>
                {/* <Title level={2}>活动管理</Title> */}
                <Row gutter={[16, 16]}>
                    <Col span={18}>
                        <Card>
                            <Title level={4} className="title-left">
                                活动信息
                            </Title>
                            <Text className="ant-typography">
                                活动标题: {activityData?.activityTitle}
                            </Text>
                        </Card>
                    </Col>
                    <Col span={18}>
                        <Card>
                            <Title level={4} className="title-left">
                                角色信息
                            </Title>
                            <div className="role-container">
                                {activityData?.activityRoles.map((role, index) => (
                                    <div key={index} className="role-item">
                                        <Text className="ant-typography">
                                            {role.role}: {role.point}分
                                        </Text>
                                    </div>
                                ))}
                            </div>
                        </Card>
                    </Col>
                    <Col span={18}>
                        <Card>
                            <Title level={4} className="title-left">
                                审核名单
                            </Title>
                            <Input.Search
                                placeholder="请输入学号查询学生"
                                className="custom-input"
                                style={{ marginBottom: 16 }}
                            />
                            <Button type="primary" style={{ marginBottom: 16 }}>
                                新增学生
                            </Button>
                            <Table
                                // rowSelection={{
                                //     type: 'checkbox',
                                // }}
                                columns={columns}
                                dataSource={activityData?.activityStudents}
                                rowKey="id"
                                loading={isLoading}
                                scroll={{ y: 800 }} // 设置滚动条高度
                                pagination={false} // 禁用分页
                            />
                        </Card>
                    </Col>
                </Row>
                <Divider />
                {/* {activityData && (
                <div>
                    <h2>活动详情</h2>
                    <p>活动标题: {activityData.activityTitle}</p>
                    <h3>角色</h3>
                    <ul>
                        {activityData.activityRoles.map((role, index) => (
                            <li key={index}>
                                {role.role}: {role.point}
                            </li>
                        ))}
                    </ul>
                    <h3>学生列表</h3>
                    <table>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>用户ID</th>
                                <th>姓名</th>
                                <th>角色</th>
                            </tr>
                        </thead>
                        <tbody>
                            {activityData.activityStudents.map(student => (
                                <tr key={student.id}>
                                    <td>{student.id}</td>
                                    <td>{student.userId}</td>
                                    <td>{student.userName}</td>
                                    <td>{student.role}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )} */}
            </div>
        </div>
    )
}

export default AuditPage
