// import React from 'react'

// function ActivityList() {
//     return (
//         <div>
//             <h2>Activity List</h2>
//             {/* Activity list content here */}
//         </div>
//     )
// }

// export default ActivityList

import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'
import { useTable, useSortBy, usePagination } from 'react-table'
import './ActivityList.css' // 引入自定义的 CSS 文件

// props 是一个对象，包含了从父组件传递过来的数据
function Greeting(props) {
    return <h1>你好, {props.name}!</h1>
}
function SortableTable({ columns, data }) {
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        page, // 代替 rows 用于分页
        prepareRow,
        canPreviousPage,
        canNextPage,
        pageOptions,
        pageCount,
        gotoPage,
        nextPage,
        previousPage,
        setPageSize,
        state: { pageIndex, pageSize },
    } = useTable(
        {
            columns,
            data,
            initialState: { pageIndex: 0 }, // 初始页码
        },
        useSortBy,
        usePagination // 使用分页插件
    )
    // 这段代码是一个React组件的一部分，用于渲染一个可排序的表格。首先，它使用<div>标签创建一个包含表格的容器，然后使用<table>标签定义表格本身，并通过getTableProps()方法应用表格的属性。

    // 在<table>内部，首先定义了表头(<thead>)部分。这里使用了headerGroups.map方法遍历headerGroups数组（这个数组由useTable钩子提供，包含了所有列的元数据），为每个表头组渲染一个<tr>行。在每个表头组的行内，再次使用.map方法遍历每个组中的列，为每列渲染一个<th>表头单元格。每个表头单元格通过column.getHeaderProps(column.getSortByToggleProps())获取必要的属性，并通过column.render('Header')渲染列标题。如果列已经被排序，还会在标题旁边显示一个上箭头或下箭头图标，表示排序的方向。

    // 接下来定义了表体(<tbody>)部分。这里使用了page.map方法遍历page数组（这个数组也是由useTable钩子提供，代表当前页的行数据），为每行数据渲染一个<tr>行。在每行内部，使用.map方法遍历行中的单元格，为每个单元格渲染一个<td>标签，并通过cell.render('Cell')渲染单元格的内容。

    // 整个过程中，useTable钩子提供的方法和属性被广泛用于获取表格的配置、状态和行为，使得这个表格组件具有了排序和分页的功能。通过这种方式，React组件能够以声明式的方式清晰地描述UI的结构和行为，同时保持代码的可读性和可维护性。
    return (
        <div className="table-container">
            <table {...getTableProps()}>
                <thead>
                    {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                                    {column.render('Header')}
                                    <span>
                                        {column.isSorted
                                            ? column.isSortedDesc
                                                ? ' 🔽'
                                                : ' 🔼'
                                            : ''}
                                    </span>
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {page.map(row => {
                        prepareRow(row)
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map(cell => (
                                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                ))}
                            </tr>
                        )
                    })}
                </tbody>
            </table>
            {/* 分页控制 */}
            <div className="pagination">
                <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
                    {'<<'}
                </button>
                <button onClick={() => previousPage()} disabled={!canPreviousPage}>
                    {'<'}
                </button>
                <button onClick={() => nextPage()} disabled={!canNextPage}>
                    {'>'}
                </button>
                <button onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}>
                    {'>>'}
                </button>
                <span>
                    Page{' '}
                    <strong>
                        {pageIndex + 1} of {pageOptions.length}
                    </strong>
                </span>
                <select
                    value={pageSize}
                    onChange={e => {
                        setPageSize(Number(e.target.value))
                    }}
                >
                    {[10, 20, 30, 40, 50].map(pageSize => (
                        <option key={pageSize} value={pageSize}>
                            Show {pageSize}
                        </option>
                    ))}
                </select>
            </div>
        </div>
    )
}

// 使用示例
const columns = [
    {
        Header: 'ID',
        accessor: 'id', // 访问器是从原始数据中取出数据的键
    },
    {
        Header: '活动标题',
        accessor: 'activityTitle',
    },
    {
        Header: '开始时间',
        accessor: 'activityStartTime',
    },
    {
        Header: '结束时间',
        accessor: 'activityEndTime',
    },
    {
        Header: '发布人',
        accessor: 'selectPublisher',
    },
    {
        Header: '活动状态',
        accessor: 'activityStatus',
    },
    {
        Header: '申请截至时间',
        accessor: 'applyEndTime',
    },
    {
        Header: '操作',
    },
]

// fetch('./public/activities.json') // 从本地资源文件中读取数据
//     .then(response => response.json())
//     .then(data2 => {
//         console.log('read json', data2)
//         data = data2.map(item => {
//             return {
//                 id: item.id,
//                 activityTitle: item.activityTitle,
//                 activityStartTime: item.activityStartTime,
//                 activityEndTime: item.activityEndTime,
//                 selectPublisher: item.selectPublisher,
//                 activityStatus: item.activityStatus,
//                 applyEndTime: item.applyDeadline,
//             }
//         })
//         setData(data)
//     })

function ActivityList() {
    const location = useLocation()
    const res = location.state || {}
    const { name: username, token } = res

    const [data, setData] = useState([])

    useEffect(() => {
        fetch('/activities.json') // 确保路径正确
            .then(response => response.json())
            .then(data => {
                console.log('read json', data)
                const formattedData = data.result.data.map(item => ({
                    id: item.id,
                    activityTitle: item.activityTitle,
                    activityStartTime: item.activityStartTime,
                    activityEndTime: item.activityEndTime,
                    selectPublisher: item.selectPublisher,
                    activityStatus: item.activityStatus,
                    applyEndTime: item.applyDeadline,
                }))
                setData(formattedData)
            })
    }, [])
    return (
        <div>
            <div>
                <h1>Activity List,</h1>
                <Greeting name={username} />
            </div>
            <div>
                <SortableTable data={data} columns={columns} />
            </div>
        </div>
    )
}

export default ActivityList
