// import React from 'react'

// function ActivityList() {
//     return (
//         <div>
//             <h2>Activity List</h2>
//             {/* Activity list content here */}
//         </div>
//     )
// }

// export default ActivityList

import React, { useState } from 'react'
import { useLocation } from 'react-router-dom'
// props 是一个对象，包含了从父组件传递过来的数据
function Greeting(props) {
    return <h1>你好, {props.name}!</h1>
}

function SortableTable({ data, columns }) {
    const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' })

    const sortedData = React.useMemo(() => {
        let sortableItems = [...data]
        if (sortConfig !== null) {
            sortableItems.sort((a, b) => {
                if (a[sortConfig.key] < b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? -1 : 1
                }
                if (a[sortConfig.key] > b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? 1 : -1
                }
                return 0
            })
        }
        return sortableItems
    }, [data, sortConfig])

    const requestSort = key => {
        let direction = 'ascending'
        if (sortConfig.key === key && sortConfig.direction === 'ascending') {
            direction = 'descending'
        }
        setSortConfig({ key, direction })
    }

    return (
        <table>
            <thead>
                <tr>
                    {columns.map(column => (
                        <th
                            key={column.key}
                            onClick={column.sortable ? () => requestSort(column.key) : null}
                            style={{ cursor: column.sortable ? 'pointer' : 'auto' }}
                        >
                            {column.name}
                        </th>
                    ))}
                </tr>
            </thead>
            <tbody>
                {sortedData.map(item => (
                    <tr key={item.id}>
                        {columns.map(column => (
                            <td key={column.key}>{item[column.key]}</td>
                        ))}
                    </tr>
                ))}
            </tbody>
        </table>
    )
}

// 使用示例
const columns = [
    { key: 'id', name: 'ID', sortable: true },
    { key: 'activityTitle', name: '活动标题', sortable: true },
    { key: 'selectPublisher', name: '发布者', sortable: false },
    { key: 'activityStartTime', name: '开始时间', sortable: true },
    { key: 'activityEndTime', name: '结束时间', sortable: true },
    { key: 'applyDeadline', name: '报名截止', sortable: true },
    { key: 'activityContent', name: '活动内容', sortable: false },
    { key: 'activityRoles', name: '活动角色', sortable: false },
    { key: 'applyStatus', name: '报名状态', sortable: true },
]

const data = [
    // 数据数组
]

function ActivityList() {
    const location = useLocation()
    const res = location.state || {}
    console.log('res', res)
    const { name: username, token } = res
    return (
        <div>
            <div>
                <h1>Activity List</h1>
                <Greeting name={username} />
            </div>
            <div>
                <SortableTable data={data} columns={columns} />
            </div>
        </div>
    )
}

export default ActivityList
