import React, { useState, useEffect } from 'react'
import { useParams, useLocation, useNavigate } from 'react-router-dom'
import { Table, Input, Button, Switch, Typography, Row, Col, Card, Divider } from 'antd'

const { Title, Text } = Typography
const testdata = {
    activityId: 971,
    activityTitle: '【学术活动】“一国两制”在香港的实践',
    activityRoles: [
        {
            role: '参与者',
            point: '0.00',
        },
        {
            role: '组织者',
            point: '0.00',
        },
    ],
    activityStudents: [
        {
            id: 36339,
            userId: '21820245',
            role: '未参与',
            userName: '黄宇喆',
            activityId: 971,
        },
        {
            id: 36388,
            userId: '22721481',
            role: '未参与',
            userName: '叶文斌',
            activityId: 971,
        },
        {
            id: 36190,
            userId: '23721459',
            role: '未参与',
            userName: '段帅',
            activityId: 971,
        },
        {
            id: 36189,
            userId: '23721461',
            role: '未参与',
            userName: '毛梦茜',
            activityId: 971,
        },
        {
            id: 36217,
            userId: '23721463',
            role: '未参与',
            userName: '范亚婷',
            activityId: 971,
        },
        {
            id: 36224,
            userId: '23721485',
            role: '未参与',
            userName: '展晨',
            activityId: 971,
        },
        {
            id: 36193,
            userId: '23721486',
            role: '未参与',
            userName: '李春洁',
            activityId: 971,
        },
        {
            id: 36225,
            userId: '23721489',
            role: '未参与',
            userName: '刘瑶',
            activityId: 971,
        },
        {
            id: 36191,
            userId: '23721499',
            role: '未参与',
            userName: '李佳美',
            activityId: 971,
        },
        {
            id: 36196,
            userId: '23721500',
            role: '未参与',
            userName: '张一丹',
            activityId: 971,
        },
        {
            id: 36194,
            userId: '23721501',
            role: '未参与',
            userName: '王波',
            activityId: 971,
        },
        {
            id: 36227,
            userId: '23721533',
            role: '未参与',
            userName: '钱磊',
            activityId: 971,
        },
        {
            id: 36374,
            userId: '23721568',
            role: '未参与',
            userName: '李盈谕',
            activityId: 971,
        },
        {
            id: 36192,
            userId: '23721578',
            role: '未参与',
            userName: '龚梦迪',
            activityId: 971,
        },
    ],
}
function AuditPage() {
    const { id } = useParams()
    const location = useLocation()
    console.log('id', id, 'state', location.state)

    const [selectedFile, setSelectedFile] = useState(null)
    const [uploadStatus, setUploadStatus] = useState('')
    const [isLoading, setIsLoading] = useState(false)
    const [activityData, setActivityData] = useState(null)
    const handleFileChange = event => {
        setSelectedFile(event.target.files[0])
    }
    const delay = ms => new Promise(resolve => setTimeout(resolve, ms))
    const handleUpload = async () => {
        if (selectedFile) {
            // 创建一个 FormData 对象
            const formData = new FormData()
            formData.append('file', selectedFile)
            setIsLoading(true)
            setUploadStatus('上传中')
            try {
                const response = await fetch('http://localhost:3000/upload', {
                    method: 'POST',
                    body: formData,
                })
                await delay(2000)

                if (response.ok) {
                    setUploadStatus(`文件 ${selectedFile.name} 上传成功！`)
                } else {
                    setUploadStatus('文件上传失败！')
                }
            } catch (error) {
                console.error('上传错误:', error)
                setUploadStatus('文件上传失败！')
            } finally {
                setIsLoading(false)
            }
        } else {
            setUploadStatus('请选择一个文件')
        }
    }
    useEffect(() => {
        // 模拟获取活动数据的请求
        async function fetchActivityData() {
            setIsLoading(true)
            try {
                const response = await fetch(`http://localhost:3000/activity/${id}`)
                const data = await response.json()
                data.result = testdata
                if (data.code === 200) {
                    setActivityData(data.result)
                }
            } catch (error) {
                console.error('获取活动数据错误:', error)
            } finally {
                setIsLoading(false)
                setActivityData(testdata)
            }
        }
        fetchActivityData()
    }, [id])
    const columns = [
        {
            title: '学号',
            dataIndex: 'userId',
            key: 'userId',
        },
        {
            title: '姓名',
            dataIndex: 'userName',
            key: 'userName',
        },
        {
            title: '当前参与角色',
            dataIndex: 'role',
            key: 'role',
        },
    ]
    return (
        <div>
            <h1>审核页面</h1>
            <p>正在审核的活动ID：{id}</p>
            <div>
                <input type="file" accept=".pdf,image/*" onChange={handleFileChange} />
                <button onClick={handleUpload}>上传文件</button>
            </div>
            {selectedFile && (
                <div>
                    <p>选中的文件: {selectedFile.name}</p>
                </div>
            )}
            {uploadStatus && (
                <div>
                    <p>{uploadStatus}</p>
                </div>
            )}
            {isLoading && (
                <div>
                    <p>加载中...</p>
                </div>
            )}
            {activityData && (
                <div>
                    <h2>活动详情</h2>
                    <p>活动标题: {activityData.activityTitle}</p>
                    <h3>角色</h3>
                    <ul>
                        {activityData.activityRoles.map((role, index) => (
                            <li key={index}>
                                {role.role}: {role.point}
                            </li>
                        ))}
                    </ul>
                    <h3>学生列表</h3>
                    <table>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>用户ID</th>
                                <th>姓名</th>
                                <th>角色</th>
                            </tr>
                        </thead>
                        <tbody>
                            {activityData.activityStudents.map(student => (
                                <tr key={student.id}>
                                    <td>{student.id}</td>
                                    <td>{student.userId}</td>
                                    <td>{student.userName}</td>
                                    <td>{student.role}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    )
}

export default AuditPage
